import { TITLE } from './types'

 const initialState = {
   likes: 0,
 }

export const titleReducer = (state, action) => {
  console.log('reducer>', action)

  switch (action.type) {
    case TITLE:
      return {
        ...state,
        title: state.value,
      }
    default:
      return state
  }
}
