import { likeReducer } from './likeReducers'
import { titleReducer } from './likeReducers copy'

export const reducers = (state, action) => {
  console.log('STATE', state)
  const initialState = {}
  if (!state)
    initialState = {
      likes: 0,
      title: '',
    }
  else
    initialState = {
      likes: state.likes,
      title: state.title,
    }
  return {
    ...likeReducer(initialState, action),
    ...titleReducer(initialState, action),
  }
}
