import { TITLE } from './types'

export const titleReducer = (state = state.title, action) => {
  console.log('reducer>', action)
  console.log('STATE',state)
  switch (action.type) {
    case TITLE:
      return {
        ...state,
        title: action.value,
      }
    default:
      return state
  }
}
