import { TITLE } from './types'

const initialState = {
  likes: 0,
}

export const titleReducer = (state = initialState, action) => {
  console.log('reducer>', action)
  console.log('STATE', state)
  switch (action.type) {
    case TITLE:
      return {
        ...state,
        title: action.value,
      }
    default:
      return state
  }
}
